document.addEventListener("DOMContentLoaded", function () {
    var passwordInput = document.querySelector("[type='password']");
    var togglePasswordButton = document.getElementById("toggle-password");

    if (passwordInput && togglePasswordButton) {
        passwordInput.classList.add("input-password");
        togglePasswordButton.classList.remove("d-none");

        togglePasswordButton.addEventListener("click", togglePassword);

        function togglePassword() {
            if (passwordInput.type === "password") {
                passwordInput.type = "text";
                togglePasswordButton.setAttribute(
                    "aria-label",
                    "Hide password."
                );
            } else {
                passwordInput.type = "password";
                togglePasswordButton.setAttribute(
                    "aria-label",
                    "Show password as plain text. " +
                        "Warning: this will display your password on the screen."
                );
            }
        }
    }
});
